import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";

export default function PracticalLawHome() {
  // 搜索输入 & 搜索结果
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  // 分类搜索结果
  const [categoryResults, setCategoryResults] = useState([]);

  // 当前是否在查看分类模式
  const [activeCategory, setActiveCategory] = useState("");

  // 最近搜索记录
  const [recentHistory, setRecentHistory] = useState([]);

  // 当页面加载时，获取最近搜索记录
  useEffect(() => {
    fetchRecentHistory();
  }, []);

  // 获取最近搜索记录
  const fetchRecentHistory = async () => {
    try {
      const response = await fetch("http://localhost:5000/history");
      const data = await response.json();
      setRecentHistory(data);
    } catch (error) {
      console.error("Failed to fetch recent history:", error);
    }
  };

  // 执行搜索
  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchQuery.trim()) {
      setSearchResults([]);
      return;
    }
    try {
      const response = await fetch(
        `http://localhost:5000/search?q=${encodeURIComponent(searchQuery)}`
      );
      const data = await response.json();
      setSearchResults(data);
      setActiveCategory(""); // 退出分类模式
      setCategoryResults([]);
      // 重新获取搜索历史
      fetchRecentHistory();
    } catch (error) {
      console.error("Search error:", error);
    }
  };

  // 点击分类时调用 /searchByCategory
  const handleCategoryClick = async (catName) => {
    setActiveCategory(catName);
    setSearchQuery(""); // 清空搜索输入
    setSearchResults([]);
    try {
      const response = await fetch(
        `http://localhost:5000/searchByCategory?category=${encodeURIComponent(catName)}`
      );
      const data = await response.json();
      setCategoryResults(data);
    } catch (error) {
      console.error("Category search error:", error);
    }
  };

  return (
    <div className="min-h-screen flex flex-col bg-gray-50">
      {/* 顶部导航栏 */}
      <nav className="bg-[#005B8E] text-white">
        <div className="container mx-auto px-4 py-3 flex items-center justify-between">
          {/* 左侧 Logo / Brand */}
          <div className="flex items-center space-x-3">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="28"
              height="28"
              fill="currentColor"
              className="bi bi-journal-text"
              viewBox="0 0 16 16"
            >
              <path
                fillRule="evenodd"
                d="M6 4V1h1v3H6zM2 1h2v14H2V1zm3 0h8v2H5V1zm8 4v9H5V5h8zm-7 2v1h6V7H6zm0 2v1h6V9H6z"
              />
            </svg>
            <span className="text-xl font-bold tracking-wide">Practical Law</span>
          </div>

          {/* 中间搜索框 */}
          <div className="hidden md:flex items-center space-x-2">
            <form onSubmit={handleSearch} className="flex">
              <input
                type="text"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                placeholder="Search Practical Law UK"
                className="w-72 px-3 py-2 rounded-l-md focus:outline-none text-gray-700"
              />
              <button
                type="submit"
                className="px-4 py-2 bg-[#FF7300] hover:bg-orange-600 rounded-r-md font-semibold"
              >
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="16"
                  height="16"
                  fill="currentColor"
                  className="inline-block mr-1 mb-0.5"
                  viewBox="0 0 16 16"
                >
                  <path d="M11.742 10.344a6.5 6.5 0 1
                           0-1.398 1.398h-.001l3.85
                           3.85a1 1 0 0 0
                           1.414-1.414l-3.85-3.85zm-5.242.656
                           a5.5 5.5 0 1 1 0-11 5.5
                           5.5 0 0 1 0 11z"/>
                </svg>
                Search
              </button>
            </form>
          </div>

          {/* 右侧导航 */}
          <div className="flex items-center space-x-6 text-sm">
            <a href="#" className="hover:text-gray-200">
              History
            </a>
            <a href="#" className="hover:text-gray-200">
              Folders
            </a>
          </div>
        </div>
      </nav>

      {/* 响应式下在导航栏下方显示搜索框 */}
      <div className="bg-[#005B8E] text-white md:hidden">
        <div className="container mx-auto px-4 py-3">
          <form onSubmit={handleSearch} className="flex">
            <input
              type="text"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              placeholder="Search Practical Law UK"
              className="flex-grow px-3 py-2 rounded-l-md focus:outline-none text-gray-700"
            />
            <button
              type="submit"
              className="px-4 py-2 bg-[#FF7300] hover:bg-orange-600 rounded-r-md font-semibold"
            >
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="16"
                height="16"
                fill="currentColor"
                className="inline-block mr-1 mb-0.5"
                viewBox="0 0 16 16"
              >
                <path d="M11.742 10.344a6.5 6.5 0 1
                         0-1.398 1.398h-.001l3.85
                         3.85a1 1 0 0 0
                         1.414-1.414l-3.85-3.85zm-5.242.656
                         a5.5 5.5 0 1 1 0-11 5.5
                         5.5 0 0 1 0 11z"/>
              </svg>
              Search
            </button>
          </form>
        </div>
      </div>

      {/* 主体内容 */}
      <div className="container mx-auto px-4 py-6 flex-1">
        <div className="flex flex-col md:flex-row md:space-x-8">
          {/* 左侧主要内容 */}
          <div className="w-full md:w-2/3 mb-6 md:mb-0">
            <div className="flex items-center justify-between mb-3">
              <motion.h2
                initial={{ opacity: 0, y: -20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6 }}
                className="text-3xl font-bold text-gray-700"
              >
                UK Home
              </motion.h2>
              <div className="inline-flex items-center space-x-4 text-blue-600">
                <a href="#" className="hover:underline">Meet the team</a>
                <a href="#" className="hover:underline">Ask a question</a>
              </div>
            </div>

            <hr className="mb-4" />

            <h4 className="text-xl font-bold text-gray-700 mb-4">Sectors &amp; Resources</h4>
            {/* 分类网格 */}
            <div className="grid grid-cols-2 sm:grid-cols-3 gap-4 text-blue-600">
              <button
                onClick={() => handleCategoryClick("Business")}
                className="text-left hover:underline"
              >
                Business
              </button>
              <button
                onClick={() => handleCategoryClick("Public Law")}
                className="text-left hover:underline"
              >
                Public Law
              </button>
              <button
                onClick={() => handleCategoryClick("Criminal")}
                className="text-left hover:underline"
              >
                Criminal
              </button>
              <button
                onClick={() => handleCategoryClick("IP & IT")}
                className="text-left hover:underline"
              >
                IP &amp; IT
              </button>
              <button
                onClick={() => handleCategoryClick("Environment")}
                className="text-left hover:underline"
              >
                Environment
              </button>
              <button
                onClick={() => handleCategoryClick("Capital Markets")}
                className="text-left hover:underline"
              >
                Capital Markets
              </button>
              <button
                onClick={() => handleCategoryClick("Restructuring & Insolvency")}
                className="text-left hover:underline"
              >
                Restructuring &amp; Insolvency
              </button>
              {/* 可继续添加更多分类 */}
            </div>

            {/* 如果是搜索模式，则显示 searchResults，否则显示 categoryResults */}
            <div className="mt-6">
              {activeCategory && (
                <h3 className="text-lg font-semibold mb-2 text-gray-700">
                  Showing results for category: {activeCategory}
                </h3>
              )}
              {searchQuery && (
                <h3 className="text-lg font-semibold mb-2 text-gray-700">
                  Search results for: {searchQuery}
                </h3>
              )}
              {searchResults.length > 0 && (
                <>
                  {searchResults.map((entry) => (
                    <div
                      key={entry.id}
                      className="bg-white p-4 rounded-xl shadow-md mb-4"
                    >
                      <h2 className="text-2xl font-bold text-gray-800">
                        {entry.term}
                      </h2>
                      <p className="text-gray-600 mt-2">{entry.definition}</p>
                      <p className="text-sm text-gray-400 mt-1">
                        Category: {entry.category}
                      </p>
                    </div>
                  ))}
                </>
              )}
              {categoryResults.length > 0 && (
                <>
                  {categoryResults.map((entry) => (
                    <div
                      key={entry.id}
                      className="bg-white p-4 rounded-xl shadow-md mb-4"
                    >
                      <h2 className="text-2xl font-bold text-gray-800">
                        {entry.term}
                      </h2>
                      <p className="text-gray-600 mt-2">{entry.definition}</p>
                      <p className="text-sm text-gray-400 mt-1">
                        Category: {entry.category}
                      </p>
                    </div>
                  ))}
                </>
              )}
            </div>
          </div>

          {/* 右侧侧栏 */}
          <div className="w-full md:w-1/3 space-y-6">
            {/* Discover Dynamic Tools */}
            <div className="border border-gray-200 rounded-lg p-4 bg-white shadow-sm">
              <h5 className="text-lg font-semibold mb-3 text-gray-700">Discover Dynamic Tools</h5>
              <p className="text-gray-600 mb-4">
                Learn about new features that allow you to explore Practical Law content in dynamic ways.
              </p>
              <button className="px-4 py-2 bg-[#FF7300] hover:bg-orange-600 text-white rounded font-semibold">
                Explore Tools
              </button>
            </div>

            {/* Recent History */}
            <div className="border border-gray-200 rounded-lg p-4 bg-white shadow-sm">
              <h5 className="text-lg font-semibold mb-3 text-gray-700">Recent History</h5>
              {recentHistory.length === 0 ? (
                <p className="text-gray-500">No recent searches.</p>
              ) : (
                recentHistory.map((item, idx) => (
                  <div key={idx} className="mb-2">
                    <p className="font-semibold text-gray-800">{item.query}</p>
                    <p className="text-sm text-gray-500">
                      {new Date(item.created_at).toLocaleString()}
                    </p>
                  </div>
                ))
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Footer */}
      <footer className="bg-gray-100 py-4">
        <div className="container mx-auto text-center text-gray-600 text-sm">
          © {new Date().getFullYear()} Practical Law. All rights reserved.
        </div>
      </footer>
    </div>
  );
}
